mapper.select:
select

from sys_income


        and income_id = #{incomeId}


        and user_id = #{userId}


        and income_type = #{incomeType}


        and income_date = #{incomeDate}


        and income_company = #{incomeCompany}


        and income_amount = #{incomeAmount}


        and user_id = #{userKey}


order by

    ${sort} ${order}


    ${sort}


    income_date desc
