mapper.select:
select '收入来源' title, '单位：万元' subtitle, s.dictionary_caption reportName, trunc(sum(t.income_amount) / 10000, 2) reportnum from sys_income t, sys_dictionary s where t.income_company = s.dictionary_code || '-' || s.dictionary_item
and t.user_id = #{userId}
group by s.dictionary_caption order by s.dictionary_caption

select '收入类型' title, '单位：万元' subtitle, s.dictionary_caption reportName, trunc(sum(t.income_amount) / 10000, 2) reportnum from sys_income t, sys_dictionary s where t.income_type = s.dictionary_code || '-' || s.dictionary_item
and t.user_id = #{userId}
group by s.dictionary_caption order by s.dictionary_caption

select '收入极值' title, '单位：元' subtitle, reportname, reportnum from (select s.dictionary_caption reportname, max(t.income_amount) reportnum from sys_income t, sys_dictionary s where t.income_type = s.dictionary_code || '-' || s.dictionary_item
and t.user_id = #{userId}
group by s.dictionary_caption order by max(t.income_amount) desc ) m where rownum = 1 union all select '收入极值' title, '单位：元' subtitle, reportname, reportnum from (select s.dictionary_caption reportname, min(t.income_amount) reportnum from sys_income t, sys_dictionary s where t.income_type = s.dictionary_code || '-' || s.dictionary_item
and t.user_id = #{userId}
group by s.dictionary_caption order by min(t.income_amount) ) s where rownum = 1

select case when #{giftMode} = '0' then '送礼类型' else '收礼类型' end title, '单位：元' subtitle, s.dictionary_caption reportName, sum(t.gift_amount) reportnum from sys_gift t, sys_dictionary s, sys_user n where t.gift_type = s.dictionary_code || '-' || s.dictionary_item
and substr(t.gift_sender, instr(t.gift_sender, '-') + 1) = n.user_id
and substr(t.gift_receiver, instr(t.gift_receiver, '-') + 1) = n.user_id
and substr(t.gift_sender, instr(gift_sender, '-') + 1) = #{giftSender}
and substr(t.gift_receiver, instr(gift_receiver, '-') + 1) = #{giftReceiver}
group by s.dictionary_caption order by s.dictionary_caption

select case when #{giftMode} = '0' then '送礼极值' else '收礼极值' end title, '单位：元' subtitle, reportname, reportnum from ( select s.dictionary_caption reportname, max(t.gift_amount) reportnum from sys_gift t, sys_dictionary s, sys_user n where t.gift_type = s.dictionary_code || '-' || s.dictionary_item
and substr(t.gift_sender, instr(t.gift_sender, '-') + 1) = n.user_id
and substr(t.gift_receiver, instr(t.gift_receiver, '-') + 1) = n.user_id
and substr(t.gift_sender, instr(gift_sender, '-') + 1) = #{giftSender}
and substr(t.gift_receiver, instr(gift_receiver, '-') + 1) = #{giftReceiver}
group by s.dictionary_caption order by max(t.gift_amount) desc ) m where rownum = 1 union all select case when #{giftMode} = '0' then '送礼极值' else '收礼极值' end title, '单位：元' subtitle, reportname, reportnum from ( select s.dictionary_caption reportname, min(t.gift_amount) reportnum from sys_gift t, sys_dictionary s, sys_user n where t.gift_type = s.dictionary_code || '-' || s.dictionary_item
and substr(t.gift_sender, instr(t.gift_sender, '-') + 1) = n.user_id
and substr(t.gift_receiver, instr(t.gift_receiver, '-') + 1) = n.user_id
and substr(t.gift_sender, instr(gift_sender, '-') + 1) = #{giftSender}
and substr(t.gift_receiver, instr(gift_receiver, '-') + 1) = #{giftReceiver}
group by s.dictionary_caption order by min(t.gift_amount) ) s where rownum = 1

select '随礼' title, '单位：元' subTitle, '送礼' reportname, sum(t.gift_amount) reportnum from sys_gift t, sys_dictionary s, sys_user n where t.gift_type = s.dictionary_code || '-' || s.dictionary_item and substr(t.gift_sender, instr(t.gift_sender, '-') + 1) = n.user_id
and substr(t.gift_sender, instr(gift_sender, '-') + 1) = #{giftSender}
union all select '随礼' title, '单位：元' subTitle, '收礼' reportname, sum(t.gift_amount) reportnum from sys_gift t, sys_dictionary s, sys_user n where t.gift_type = s.dictionary_code || '-' || s.dictionary_item and substr(t.gift_receiver, instr(t.gift_receiver, '-') + 1) = n.user_id
and substr(t.gift_receiver, instr(gift_receiver, '-') + 1) = #{giftReceiver}
