mapper.select:
select

from sys_salary


        and salary_id = #{salaryId}


        and user_id = #{userId}


        and salary_type = #{salaryType}


        and salary_date = #{salaryDate}


        and salary_company = #{salaryCompany}


        and salary_amount = #{salaryAmount}


        and user_id = #{userKey}


order by

    ${sort} ${order}


    ${sort}


    salary_date desc
